import axios from "axios";

const API_TOKEN = "e05eb5ca356578e9f652d58f443ab993";

const getFilmsFromApiWithSearchedText = (text) => {
    const url = 'https://api.themoviedb.org/3/search/movie?api_key=' + API_TOKEN + '&language=fr&query=' + text
    return axios.get(url).then(res => res.data).catch(err => console.log(err))
  }

export default getFilmsFromApiWithSearchedText;
