import React, {useState} from 'react'
import {StyleSheet,View,TextInput,FlatList,Button} from 'react-native'
import FilmItem from './FilmItem'
import load from '../../API/TMDBApi'

const Search = () => {

    const [films,setFilms] = useState([])
    var searchedFilm = ""

    var load_film = () => {
      load(searchedFilm).then(res => {
        console.log(res.data.results)
        setFilms(res.data.results)
      })
    }

    const pressed = () => {
        console.log("Rechercher pressed")
        load_film();
    }

    return(
        <View style={styles.main_container}>
            <TextInput 
              placeholder='Titre du film'
              style={styles.textinput}
              onChangeText= {(text) =>searchedFilm = text} 
              onSubmitEditing = {() => load_film()} />
            <Button title='Rechercher' onPress={pressed}/>
            <FlatList
          data={films}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({item}) => <FilmItem props={item}/>}
        />
        </View>
    )
}

// Components/Search.js

const styles = StyleSheet.create({
    textinput: {
      marginLeft: 5,
      marginRight: 5,
      height: 50,
      borderColor: '#000000',
      borderWidth: 1,
      paddingLeft: 5
    },
    main_container :{
        flex:1,
        marginTop:20
    }
  })

export default Search
