import React, {useRef, useState} from 'react'
import {StyleSheet,View,TextInput,FlatList,Button, ActivityIndicator} from 'react-native'
import FilmItem from './FilmItem'
import load from '../../API/TMDBApi'

const Search = () => {

    const [films,setFilms] = useState([])
    const [loading,setLoading] = useState(false)
    var searchedFilm = ""
    const c_page = useRef(0)
    const total_page= useRef(0)

    var load_film = () => {
      if(searchedFilm.length > 0){
      setLoading(true)
      load(searchedFilm,c_page.current).then(res => {
        setFilms(films.concat(res.data.results))
        setLoading(false)
        console.log(c_page)
        c_page.current = res.data.page
        total_page.current = res.data.total_pages
      })
      .catch((err) => console.error(err))
    }
    }

    const pressed = () => {
        resetState()
        load_film()
    }

    const resetState = () => {
      c_page.current = 0
      total_page.current = 0
      setFilms([])
    }

    const displayLoad = () => {
      if(loading){
        return(
          <View style={styles.loading}>
            <ActivityIndicator size="large"/>
          </View>
        )
      }
    }
    return(
        <View style={styles.main_container}>
            <TextInput 
              placeholder='Titre du film'
              style={styles.textinput}
              onChangeText= {(text) =>searchedFilm = text} 
              onSubmitEditing = {() =>{
                resetState()
                load_film()
              } } />
            <Button title='Rechercher' onPress={pressed}/>
            <FlatList
            data={films}
            keyExtractor={(item) => item.id.toString()}
            renderItem={({item}) => <FilmItem props={item}/>}
            onEndReachedThreshold={0.3}
            onEndReached={ () => {
              if(c_page.current < total_page.current){

                load_film()
              }
              }
            }/>
            {displayLoad()}
        </View>
    )
}

// Components/Search.js

const styles = StyleSheet.create({
    textinput: {
      marginLeft: 5,
      marginRight: 5,
      height: 50,
      borderColor: '#000000',
      borderWidth: 1,
      paddingLeft: 5
    },
    main_container :{
        flex:1,
        marginTop:20
    },
    loading :{
      position: 'absolute',
      left: 0,
      right: 0,
      top: 100,
      bottom: 0,
      alignItems: 'center',
      justifyContent: 'center'
    }
  })

export default Search
