import React from 'react'
import films from '../helpers/filmsData'
import {StyleSheet,View,TextInput,FlatList,Button} from 'react-native'
import FilmItem from './FilmItem'
import load from '../../API/TMDBApi'

const Search = () => {

    var all_films = []

    const pressed = () => {
        console.log("Rechercher pressed")
    }

    const _loadFilms = () => {
      all_films = load("Star")
      console.log("Resultat",all_films[0])
    }

    _loadFilms()

    return(
        <View style={styles.main_container}>
            <TextInput placeholder='Titre du film' style={styles.textinput}/>
            <Button title='Rechercher' onPress={pressed}/>
            <FlatList
          data={films}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({item}) => <FilmItem props={item}/>}
        />
        </View>
    )
}

// Components/Search.js

const styles = StyleSheet.create({
    textinput: {
      marginLeft: 5,
      marginRight: 5,
      height: 50,
      borderColor: '#000000',
      borderWidth: 1,
      paddingLeft: 5
    },
    main_container :{
        flex:1,
        marginTop:20
    }
  })

export default Search
